import requests

url = "https://phone-and-email-validator1.p.rapidapi.com/email"
querystring = {"dns": "true", "blacklist": "true", "email": "test@example.com", "format": "true", "smtp": "true"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "phone-and-email-validator1.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

