import requests

url = "https://blogsapi.p.rapidapi.com/"
querystring = {"ordering": "-date_published"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "blogsapi.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

