import requests

url = "https://songmeanings.p.rapidapi.com/"
querystring = {"artist_name": "", "lyric_title": "", "sm_lid": "", "parent_id": "", "type": "all", "page": "1", "page_size": "25", "page_order": "date", "page_sort": "asc"}

