import requests

url = "https://exchange-oracle.p.rapidapi.com/convert"
querystring = {"fromCurrency": "USD", "toCurrency": "AED", "amount": "2500"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "exchange-oracle.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

