import requests

url = "https://mortgage-calculator2.p.rapidapi.com/getMonthlyPayment"
querystring = {"pmi": "0.22", "loanTerm": "15", "homeInsurance": "2000", "downPaymentPct": "20", "annualInterestRate": "4.125", "extraMonthlyPrincipalPmt": "100", "propertyTax": "6200", "downPaymentAmt": "60000", "price": "300000"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "mortgage-calculator2.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

