import requests

url = "https://sais-stock-analyzer.p.rapidapi.com/FindStocks"
querystring = {"search_term": "Apple", "exchange": "NMS"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "sais-stock-analyzer.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

