import requests

url = "https://geocoder11.p.rapidapi.com/geocode/searchByQuery"
querystring = {"lang": "de", "displayFormatDelimiter": ", ", "query": "Quellenstraße 55 1100 Wien", "visualization": false, "addAttributeMatch": false}

