import requests

url = "https://geodisasters.p.rapidapi.com/query"
querystring = {"from": "2023-05-24", "format": "geojson", "to": "2023-05-31"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "geodisasters.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

