import requests

url = "https://fonthint.p.rapidapi.com/font/{id}/hint"
querystring = {"id": "", "type": "windows-gdi", "adjustsubglyphs": "1", "binaryresult": "0", "hintcomposites": "1"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "fonthint.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

