import requests

url = "https://nowpayments.p.rapidapi.com/v1/payment/"
querystring = {"dateFrom": "2020-01-01", "page": "", "limit": 10, "sortBy": "created_at", "orderBy": "asc", "dateTo": "2021-01-01"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "nowpayments.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

