querystring = {"sport": "football", "date": "2021-10-15"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "viperscore.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

