import requests

url = "https://qr-generator-api.p.rapidapi.com/api/qrcode/generate"
querystring = {"pixelsPerModule": "10", "backColor": "#ffffff", "foreColor": "#000000", "text": "enter-your-text-here"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "qr-generator-api.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

