import requests

url = "https://qr-vcard-generator.p.rapidapi.com/vcard"
querystring = {"size": "20", "border": "2", "phone": "+1-202-555-0152", "name": "John Doe", "email": "info@example.com"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "qr-vcard-generator.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

