import requests

url = "https://translate-all-languages.p.rapidapi.com/translate"
querystring = {"text": "If you don’t know what language the text is written in, our API will detect the language of the original request.", "fromLang": "en", "toLang": "fr"}

headers = {
            "X-RapidAPI-Key": "SIGN-UP-FOR-KEY",
            "X-RapidAPI-Host": "translate-all-languages.p.rapidapi.com"
        }

response = requests.get(url, headers=headers, params=querystring)
print(response.json())

